import React from 'react'
import "./write.css"
import { useState,useContext } from 'react'
import axios from 'axios'
import { Context } from '../context/Context'
export default function Write() {
    const [title,setTitle]=useState("")
    const [desc,setDesc]=useState("")
    const [file,setFile]=useState("")
    const {user}=useContext(Context)

    const handleSubmit = async (e)=>{
        e.preventDefault();
        const newPost ={
            username: user.username,
            title,
            desc,
        };
        if(file)
        {
            const data =FormData();
            const filename= Date().now() + file.name;
            data.append("name",filename);
            data.append("file",file);
            newPost.photo =filename;
            try{
                await axios.post("/upload",data);
            }
            catch(err)
            {
                console.log(err);
            }
        }
        try{
            const res=await axios.post("/posts",newPost);
            window.location.replace("/post/"+res.data._id);
        }
        catch(err)
        {
                console.log(err);
        }

    }

    return (
        <div className="write">
            {
                file &&
                <img src={URL.createObjectURL(file)} alt="" className="writeImage" />
            }
            <form action="" className="writeForm" onSubmit={handleSubmit} >
                <div className="writeFormGroup">
                    <label htmlFor="fileInput">
                        <i className="writeIcon fas fa-plus-square"></i>
                    </label>
                    <input type="file" id="fileInput" style={{display:"none"}} onChange={e=>setFile(e.target/file[0])}/> 
                    <input type="text" placeholder="Title" className="writeInput" autoFocus={true} /> 
                </div>
                <div className="writeFormGroup">
                    <textarea name="" id="" cols="30" rows="3" placeholder="Tell your Tale...!!" className="writeTextArea"></textarea>
                </div>
                <button className="writeSubmit" type="submit">Publish</button>
            </form>
        </div>
    )
}
