import React from 'react'
import { useLocation} from 'react-router-dom'
import { useEffect,useState } from 'react';
import "./singlePost.css"
import axios from 'axios';


export default function SinglePost() {
    const [post,setPost]=useState([]);
    const location=useLocation();
    const id=location.pathname.split("/")[2];

    useEffect(() => {
        const getPost = async ()=>{
            const res= await axios.get("/posts/"+id);
            console.log(res)
        }

        getPost();
    }, [])

    return (
        <div className="singlePost">
            <div className="singlePostWrapper">
                <img className="singlePostImage" src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRozQ1BI0o0mHT42QRo8MB7ypxMw37GiIJsdg&usqp=CAU" alt="" />
                <h1 className="singlePostTitle">
                    Lorem, ipsum dolor sit amet 
                </h1>
                <div className="singlePostInfo">
                    <span className="singlePostAuthour">Authour : Anish</span>
                    <span className="singlePostDate">1/1/2022</span>

                </div>
                <p className="singlePostDesc">Lorem ipsum dolor sit amet consectetur adipisicing elit. Laudantium esse iste pariatur maiores voluptas aut et, consectetur velit culpa, possimus commodi. Quaerat, hic. Asperiores facilis unde iste aut culpa! Eaque.</p>
                <div className="singlePostEdit">
                    <i className="far fa-edit"></i>
                    <br />
                    <i className="fas fa-trash"></i>    
                </div>
            </div>

        </div>
    )
}
